		private enum ValidOperatorsQuestionA
		{
			Add,
			Multiply
		}

		private class Operation
		{
			public long Result { get; set; } = 0;
			public List<int> ElementsOfEquation { get; set; } = [];
		}

		[TestCase(Example, 3749)]
		[TestCase(Data, 1430271835320)]
		[Parallelizable]
		public void QuestionA(string data, long expected)
		{
			long solution = 0;

			List<Operation> operations = ParseData(data);
			foreach (Operation operation in operations)
			{
				if (IsValidWithMissingOperators(operation))
				{
					solution += operation.Result;
				}
			}

			Console.WriteLine(solution);
			Assert.That(solution, Is.EqualTo(expected));
		}

		private static List<Operation> ParseData(string data)
		{
			List<Operation> result = [];

			var incompleteOperationStrings = data.Split("\r\n");
			foreach (var operationString in incompleteOperationStrings)
			{
				var parts = operationString.Split(':');

				Operation operation = new()
				{
					Result = Convert.ToInt64(parts[0])
				};

				var elements = parts[1].Trim().Split(" ");
				foreach (var element in elements)
				{
					operation.ElementsOfEquation.Add(Convert.ToInt32(element));
				}

				result.Add(operation);
			}

			return result;
		}

		private static bool IsValidWithMissingOperators(Operation operation)
		{
			int numberOfOperators = operation.ElementsOfEquation.Count - 1; // operation.ElementsOfEquation.Count.Count - 1 because we can't put the operator after the last element

			var allAddition = GenerateListOfAdditionOperators(numberOfOperators);
			var onlyFirstMultiply = GenerateListOfAdditionOperators(numberOfOperators);
			onlyFirstMultiply[0] = ValidOperatorsQuestionA.Multiply;

			List<List<ValidOperatorsQuestionA>> operatorCombinations =
			[
				.. GenerateAllPotentialOperatorCombinations(allAddition, numberOfOperators, 0),
				.. GenerateAllPotentialOperatorCombinations(onlyFirstMultiply, numberOfOperators, 0),
			];
			operatorCombinations.Add(allAddition);
			operatorCombinations.Add(onlyFirstMultiply);

			foreach (var operators in operatorCombinations)
			{
				if (IsValidCalculation(operation, operators))
				{
					return true;
				}
			}

			return false;
		}

		private static List<ValidOperatorsQuestionA> GenerateListOfAdditionOperators(int count)
		{
			List<ValidOperatorsQuestionA> result = [];

			for (long i = 0; i < count; ++i)
			{
				result.Add(ValidOperatorsQuestionA.Add);
			}

			return result;
		}

		private static List<List<ValidOperatorsQuestionA>> GenerateAllPotentialOperatorCombinations(List<ValidOperatorsQuestionA> operatorSet, int numberOfOperators, int currentIndex)
		{
			// Beware!  Full-on recursion (madness) implemented here! Beautiful madness!

			List<List<ValidOperatorsQuestionA>> result = [];

			List<ValidOperatorsQuestionA> copyWithJustIndexChanged = [.. operatorSet];

			int newIndex = currentIndex + 1;
			if (newIndex < numberOfOperators)
			{
				copyWithJustIndexChanged[newIndex] = ValidOperatorsQuestionA.Multiply;
				result.Add(copyWithJustIndexChanged);

				result.AddRange(GenerateAllPotentialOperatorCombinations(operatorSet, numberOfOperators, newIndex));
				result.AddRange(GenerateAllPotentialOperatorCombinations(copyWithJustIndexChanged, numberOfOperators, newIndex));
			}

			return result;
		}

		private static bool IsValidCalculation(Operation operation, List<ValidOperatorsQuestionA> operators)
		{
			long currentResult = 0;

			switch (operators[0])
			{
				case ValidOperatorsQuestionA.Add:
					currentResult = operation.ElementsOfEquation[0] + operation.ElementsOfEquation[1];
					break;
				case ValidOperatorsQuestionA.Multiply:
					currentResult = operation.ElementsOfEquation[0] * operation.ElementsOfEquation[1];
					break;
			}

			for (int i = 1; i < operation.ElementsOfEquation.Count - 1; i++)
			{
				switch (operators[i])
				{
					case ValidOperatorsQuestionA.Add:
						currentResult += operation.ElementsOfEquation[i + 1];
						break;
					case ValidOperatorsQuestionA.Multiply:
						currentResult *= operation.ElementsOfEquation[i + 1];
						break;
				}
			}

			return currentResult == operation.Result;
		}

